import React,{FunctionComponent,useState} from 'react'; // On rajoute useState
import Pokemon from './models/pokemon';
import POKEMONS from './models/mock-pokemon';

// On type le composant App ave React.FC (Composant peut être fonction ou classe)
const App: FunctionComponent = () => { // FC = FunctionCompenent
 const[pokemons] = useState<Pokemon[]>(POKEMONS) // On utilise un State au lieu d'une const avec useState // <> = Typage pour TypeScript

 return (
  <div>
      <h1>Pokédex</h1>
      <p>Il y a</p>
  </div> // Afficher la const name dans le DOM virtuel avec JSX
 )
}

export default App; // Exporter le composant pour l'utiliser ailleurs dans l'app
